// src/components/Dashboard.js
import React, { useEffect, useState } from 'react';
import axiosInstance from '../utils/axiosInstance';
import PatentCard from './PatentCard';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [patents, setPatents] = useState([]);
  const [stats, setStats] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPatents = async () => {
      try {
        const response = await axiosInstance.get('/patents', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`
          }
        });
        setPatents(response.data);
      } catch (error) {
        console.error('Error fetching patents', error);
      }
    };

    const fetchStats = async () => {
      try {
        const response = await axiosInstance.get('/', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`
          }
        });
        setStats(response.data);
      } catch (error) {
        console.error('Error fetching patent stats', error);
      }
    };

    fetchPatents();
    fetchStats();
  }, []);

  const handleDelete = (id) => {
    setPatents(patents.filter(patent => patent.id !== id));
  };

  const handleUpdate = (id) => {
    navigate(`/patents/${id}/update`);
  };

  return (
    <div className="dashboard">
      <h1>Dashboard</h1>
      <div className="stats">
        <p>Granted: {stats.granted || 0}</p>
        <p>Pending: {stats.pending || 0}</p>
        <p>Rejected: {stats.rejected || 0}</p>
        <p>Expired: {stats.expired || 0}</p>
        <p>Abandoned: {stats.abandoned || 0}</p>
      </div>
      <div className="patent-list">
        {patents.map(patent => (
          <PatentCard key={patent.id} patent={patent} onDelete={handleDelete} onUpdate={handleUpdate} />
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
