import React, { useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axiosInstance from '../utils/axiosInstance';
import { UserContext } from '../context/UserContext';

const CreatePatentForm = ({ onPatentCreated }) => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [status, setStatus] = useState('Pending');
    const [users, setUsers] = useState([]);
    const [newUsername, setNewUsername] = useState('');
    const [patentId, setPatentId] = useState(null); // Added patentId state
    const { user } = useContext(UserContext);

    useEffect(() => {
        if (user?.username && !users.includes(user.username)) {
            setUsers([user.username, ...users]);
        }
    }, [user, users]);

    const handleAddUser = () => {
        if (newUsername && !users.includes(newUsername)) {
            setUsers([...users, newUsername]);
            setNewUsername('');
        }
    };

    const handleRemoveUser = (username) => {
        if (!username) return;
        setUsers(users.filter(user => user !== username));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const createData = {
                title,
                description,
                status,
                users: users // Users list already includes the logged-in user
            };
            const response = await axiosInstance.post('/patents', createData, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('token')}`
                }
            });
            const newPatentId = response.data.patent_id; // Extract the patent ID from the response
            setPatentId(newPatentId); // Set the patentId state
            if (newPatentId) {
                onPatentCreated(newPatentId);
            } else {
                console.error('No patent ID received:', newPatentId);
            }
        } catch (error) {
            console.error('Error creating patent', error);
        }
    };

    const handleRemoveUserFromBackend = async (username) => {
        if (!username) return;
        try {
            await axiosInstance.post(`/patents/${patentId}/remove_user`, { username }, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('token')}`
                }
            });
            handleRemoveUser(username);
            alert(`User ${username} removed successfully!`);
        } catch (error) {
            console.error('Error removing user', error);
            alert('Failed to remove user. Please try again.');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>Title:</label>
                <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} required />
            </div>
            <div>
                <label>Description:</label>
                <textarea value={description} onChange={(e) => setDescription(e.target.value)} required></textarea>
            </div>
            <div>
                <label>Status:</label>
                <select value={status} onChange={(e) => setStatus(e.target.value)} required>
                    <option value="Pending">Pending</option>
                    <option value="Granted">Granted</option>
                    <option value="Rejected">Rejected</option>
                    <option value="Expired">Expired</option>
                    <option value="Abandoned">Abandoned</option>
                </select>
            </div>
            <div>
                <label>Users:</label>
                <input
                    type="text"
                    value={newUsername}
                    onChange={(e) => setNewUsername(e.target.value)}
                />
                <button type="button" onClick={handleAddUser}>Add User</button>
                <ul>
                    {users.map((username, index) => (
                        <li key={index}>
                            {username} {username === user?.username ? '(Current User)' : ''}
                            <button type="button" onClick={() => handleRemoveUserFromBackend(username)}>Remove</button>
                        </li>
                    ))}
                </ul>
            </div>
            <button type="submit">Create Patent</button>
        </form>
    );
};

export default CreatePatentForm;
